	id: '01-how-uniswap-works',
	title: 'How Uniswap works',
	tags: 'protocol-overview, documentation',
	slug: '/'
};
export const metadata = {
  "unversionedId": "01-protocol-overview/01-how-uniswap-works",
  "id": "01-protocol-overview/01-how-uniswap-works",
  "isDocsHomePage": false,
  "title": "How Uniswap works",
  "description": "Uniswap is an automated liquidity protocol powered by a constant product formula",
  "source": "@site/docs/01-protocol-overview/01-how-uniswap-works.md",
