export const toc = [{
  value: 'Process',
  id: 'process',
  children: [{
    value: 'Phase 1: Temperature Check — Discourse/Snapshot',
    id: 'phase-1-temperature-check--discoursesnapshot',
    children: []
  }, {
    value: 'Phase 2: Consensus Check — Discourse/Snapshot',
    id: 'phase-2-consensus-check--discoursesnapshot',
    children: []
  }, {
    value: 'Phase 3: Governance Proposal — Governance Portal',
    id: 'phase-3-governance-proposal--governance-portal',
    children: []
  }]
}, {
  value: 'Soft governance',
  id: 'soft-governance',
