/* @jsx mdx */

export const toc = [{
  value: 'ES6 (import)',
  id: 'es6-import',
  children: []
}, {
  value: 'CommonJS (require)',
  id: 'commonjs-require',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const Link = makeShortcode("Link");
const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`The Uniswap SDK exists to help developers build on top of Uniswap. It's designed to run in any environment that can execute JavaScript (think websites, node scripts, etc.). While simple enough to use in a hackathon project, it's also robust enough to power production applications.`}</p>
    <h1 {...{
      "id": "installation"
    }}>{`Installation`}</h1>
    <p>{`The easiest way to consume the SDK is via npm. To install it in your project, simply run `}<inlineCode parentName="p">{`yarn add @uniswap/sdk`}</inlineCode>{` (or `}<inlineCode parentName="p">{`npm install @uniswap/sdk`}</inlineCode>{`).`}</p>
    <h1 {...{
      "id": "usage"
    }}>{`Usage`}</h1>
    <p>{`To run code from the SDK in your application, use an `}<inlineCode parentName="p">{`import`}</inlineCode>{` or `}<inlineCode parentName="p">{`require`}</inlineCode>{` statement, depending on which your environment supports. Note that the guides following this page will use ES6 syntax.`}</p>
    <h2 {...{
      "id": "es6-import"
    }}>{`ES6 (import)`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-typescript"
      }}>{`import { ChainId } from '@uniswap/sdk'
console.log(\`The chainId of mainnet is \${ChainId.MAINNET}.\`)
`}</code></pre>
    <h2 {...{
      "id": "commonjs-require"
    }}>{`CommonJS (require)`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-typescript"
      }}>{`const UNISWAP = require('@uniswap/sdk')
console.log(\`The chainId of mainnet is \${UNISWAP.ChainId.MAINNET}.\`)
`}</code></pre>
    <h1 {...{
      "id": "reference"
    }}>{`Reference`}</h1>
    <p>{`Comprehensive reference material for the SDK is available `}<Link to='/docs/v2/SDK' mdxType="Link">{`here`}</Link>{`.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
