  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Library',
	tags: 'smart-contracts, documentation'
};
export const metadata = {
  "unversionedId": "reference/smart-contracts/04-library",
  "id": "version-V1/reference/smart-contracts/04-library",
  "isDocsHomePage": false,
  "title": "Library",
  "description": "Code",
  "source": "@site/docs_versioned_docs/version-V1/reference/smart-contracts/04-library.md",
  "slug": "/reference/smart-contracts/04-library",
  "permalink": "/docs/reference/smart-contracts/04-library",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs_versioned_docs/version-V1/reference/smart-contracts/04-library.md",
  "version": "V1",
