};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'sortTokens',
  id: 'sorttokens',
  children: []
}, {
  value: 'pairFor',
  id: 'pairfor',
  children: []
}, {
  value: 'getReserves',
  id: 'getreserves',
  children: []
}, {
  value: 'quote',
  id: 'quote',
  children: []
}, {
  value: 'getAmountOut',
  id: 'getamountout',
  children: []
}, {
  value: 'getAmountIn',
  id: 'getamountin',
  children: []
}, {
  value: 'getAmountsOut',
  id: 'getamountsout',
  children: []
}, {
  value: 'getAmountsIn',
  id: 'getamountsin',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const Link = makeShortcode("Link");
const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "code"
    }}>{`Code`}</h1>
    <p><a parentName="p" {...{
        "href": "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/libraries/UniswapV2Library.sol"
      }}><inlineCode parentName="a">{`UniswapV2Library.sol`}</inlineCode></a></p>
    <h1 {...{
      "id": "internal-functions"
    }}>{`Internal Functions`}</h1>
    <h2 {...{
      "id": "sorttokens"
    }}>{`sortTokens`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1);
`}</code></pre>
    <p>{`Sorts token addresses.`}</p>
    <h2 {...{
      "id": "pairfor"
    }}>{`pairFor`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function pairFor(address factory, address tokenA, address tokenB) internal pure returns (address pair);
`}</code></pre>
    <p>{`Calculates the address for a pair without making any external calls (see `}<Link to='/docs/v2/javascript-SDK/getting-pair-addresses/' mdxType="Link">{`Pair Addresses`}</Link>{`).`}</p>
    <h2 {...{
      "id": "getreserves"
    }}>{`getReserves`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function getReserves(address factory, address tokenA, address tokenB) internal view returns (uint reserveA, uint reserveB);
`}</code></pre>
    <p>{`Calls `}<Link to='/docs/v2/smart-contracts/pair#getreserves' mdxType="Link">{`getReserves`}</Link>{` on the pair for the passed tokens, and returns the results sorted in the order that the parameters were passed in.`}</p>
    <h2 {...{
      "id": "quote"
    }}>{`quote`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function quote(uint amountA, uint reserveA, uint reserveB) internal pure returns (uint amountB);
`}</code></pre>
    <p>{`Given some asset amount and reserves, returns an amount of the other asset representing equivalent value.`}</p>
    <ul>
      <li parentName="ul">{`Useful for calculating optimal token amounts before calling `}<Link to='/docs/v2/smart-contracts/pair#mint-1' mdxType="Link">{`mint`}</Link>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "getamountout"
    }}>{`getAmountOut`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) internal pure returns (uint amountOut);
`}</code></pre>
    <p>{`Given an `}<em parentName="p">{`input`}</em>{` asset amount, returns the maximum `}<em parentName="p">{`output`}</em>{` amount of the other asset (accounting for fees) given reserves.`}</p>
    <ul>
      <li parentName="ul">{`Used in `}<a parentName="li" {...{
          "href": "#getamountsout"
        }}>{`getAmountsOut`}</a>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "getamountin"
    }}>{`getAmountIn`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) internal pure returns (uint amountIn);
`}</code></pre>
    <p>{`Returns the minimum `}<em parentName="p">{`input`}</em>{` asset amount required to buy the given `}<em parentName="p">{`output`}</em>{` asset amount (accounting for fees) given reserves.`}</p>
    <ul>
      <li parentName="ul">{`Used in `}<a parentName="li" {...{
          "href": "#getamountsin"
        }}>{`getAmountsIn`}</a>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "getamountsout"
    }}>{`getAmountsOut`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function getAmountsOut(uint amountIn, address[] memory path) internal view returns (uint[] memory amounts);
`}</code></pre>
    <p>{`Given an `}<em parentName="p">{`input`}</em>{` asset amount and an array of token addresses, calculates all subsequent maximum `}<em parentName="p">{`output`}</em>{` token amounts by calling `}<a parentName="p" {...{
        "href": "#getreserves"
      }}>{`getReserves`}</a>{` for each pair of token addresses in the path in turn, and using these to call `}<a parentName="p" {...{
        "href": "#getamountout"
      }}>{`getAmountOut`}</a>{`.`}</p>
    <ul>
      <li parentName="ul">{`Useful for calculating optimal token amounts before calling `}<Link to='/docs/v2/smart-contracts/pair#swap-1' mdxType="Link">{`swap`}</Link>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "getamountsin"
    }}>{`getAmountsIn`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function getAmountsIn(uint amountOut, address[] memory path) internal view returns (uint[] memory amounts);
`}</code></pre>
    <p>{`Given an `}<em parentName="p">{`output`}</em>{` asset amount and an array of token addresses, calculates all preceding minimum `}<em parentName="p">{`input`}</em>{` token amounts by calling `}<a parentName="p" {...{
        "href": "#getreserves"
      }}>{`getReserves`}</a>{` for each pair of token addresses in the path in turn, and using these to call `}<a parentName="p" {...{
        "href": "#getamountin"
      }}>{`getAmountIn`}</a>{`.`}</p>
    <ul>
      <li parentName="ul">{`Useful for calculating optimal token amounts before calling `}<Link to='/docs/v2/smart-contracts/pair#swap-1' mdxType="Link">{`swap`}</Link>{`.`}</li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
