  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Pair (ERC-20)',
	tags: 'smart-contracts, documentation'
};
export const metadata = {
  "unversionedId": "reference/reference/smart-contracts/03-pair-erc-20",
  "id": "reference/reference/smart-contracts/03-pair-erc-20",
  "isDocsHomePage": false,
  "title": "Pair (ERC-20)",
  "description": "This documentation covers ERC-20 functionality for denominating pool tokens. For Uniswap-specific functionality, see Pair.",
  "source": "@site/docs/reference/reference/smart-contracts/03-pair-erc-20.md",
  "slug": "/reference/reference/smart-contracts/03-pair-erc-20",
  "permalink": "/docs/reference/reference/smart-contracts/03-pair-erc-20",
