  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Glossary',
	tags: 'goveranance, documentation'
};
export const metadata = {
  "unversionedId": "v2/11-governance/04-glossary",
