  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Pair Addresses'
};
export const metadata = {
  "unversionedId": "v2/05-javascript-SDK/05-getting-pair-addresses",
  "id": "v2/05-javascript-SDK/05-getting-pair-addresses",
  "isDocsHomePage": false,
  "title": "Pair Addresses",
  "description": "getPair",
  "source": "@site/docs/v2/05-javascript-SDK/05-getting-pair-addresses.md",
  "slug": "/v2/05-javascript-SDK/05-getting-pair-addresses",
  "permalink": "/docs/v2/05-javascript-SDK/05-getting-pair-addresses",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs/v2/05-javascript-SDK/05-getting-pair-addresses.md",
  "version": "current"
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Examples',
  id: 'examples',
  children: [{
    value: 'TypeScript',
    id: 'typescript',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const Link = makeShortcode("Link");
const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "getpair"
    }}>{`getPair`}</h1>
    <p>{`The most obvious way to get the address for a pair is to call `}<Link to='/docs/v2/smart-contracts/factory/#getpair' mdxType="Link">{`getPair`}</Link>{` on the factory. If the pair exists, this function will return its address, else `}<inlineCode parentName="p">{`address(0)`}</inlineCode>{` (`}<inlineCode parentName="p">{`0x0000000000000000000000000000000000000000`}</inlineCode>{`).`}</p>
    <ul>
      <li parentName="ul">{`The "canonical" way to determine whether or not a pair exists.`}</li>
      <li parentName="ul">{`Requires an on-chain lookup.`}</li>
    </ul>
    <h1 {...{
      "id": "create2"
    }}>{`CREATE2`}</h1>
    <p>{`Thanks to some `}<a parentName="p" {...{
        "href": "https://github.com/Uniswap/uniswap-v2-core/blob/master/contracts/UniswapV2Factory.sol#L32"
      }}>{`fancy footwork in the factory`}</a>{`, we can also compute pair addresses `}<em parentName="p">{`without any on-chain lookups`}</em>{` because of `}<a parentName="p" {...{
        "href": "https://eips.ethereum.org/EIPS/eip-1014"
      }}>{`CREATE2`}</a>{`. The following values are required for this technique:`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "left"
          }}></th>
          <th parentName="tr" {...{
            "align": "left"
          }}></th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`address`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}>{`The `}<Link to='/docs/v2/smart-contracts/factory/#address' mdxType="Link">{`factory address`}</Link></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`salt`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`keccak256(abi.encodePacked(token0, token1))`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`keccak256(init_code)`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": "left"
          }}><inlineCode parentName="td">{`0x96e8ac4277198ff8b6f785478aa9a39f403cb768dd02cbee326c3e7da348845f`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`token0`}</inlineCode>{` must be strictly less than `}<inlineCode parentName="li">{`token1`}</inlineCode>{` by sort order.`}</li>
    </ul>
    <ul>
      <li parentName="ul">{`Can be computed offline.`}</li>
      <li parentName="ul">{`Requires the ability to perform `}<inlineCode parentName="li">{`keccak256`}</inlineCode>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "examples"
    }}>{`Examples`}</h2>
    <h3 {...{
      "id": "typescript"
    }}>{`TypeScript`}</h3>
    <p>{`This example makes use of the `}<Link to='docs/v2/SDK/getting-started' mdxType="Link">{`Uniswap SDK`}</Link>{`. In reality, the SDK computes pair addresses behind the scenes, obviating the need to compute them manually like this.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-typescript"
      }}>{`import { FACTORY_ADDRESS, INIT_CODE_HASH } from '@uniswap/sdk'
import { pack, keccak256 } from '@ethersproject/solidity'
import { getCreate2Address } from '@ethersproject/address'

const token0 = '0xCAFE000000000000000000000000000000000000' // change me!
const token1 = '0xF00D000000000000000000000000000000000000' // change me!

const pair = getCreate2Address(
  FACTORY_ADDRESS,
  keccak256(['bytes'], [pack(['address', 'address'], [token0, token1])]),
  INIT_CODE_HASH
)
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
