  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Getting Started',
	tags: 'sdk, documentation'
};
export const metadata = {
  "unversionedId": "v2/08-SDK/01-getting-started",
  "id": "v2/08-SDK/01-getting-started",
  "isDocsHomePage": false,
  "title": "Getting Started",
  "description": "The pages that follow contain technical reference information on the Uniswap SDK.",
  "source": "@site/docs/v2/08-SDK/01-getting-started.md",
  "slug": "/v2/08-SDK/01-getting-started",
  "permalink": "/docs/v2/08-SDK/01-getting-started",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs/v2/08-SDK/01-getting-started.md",
  "version": "current"
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Code',
  id: 'code',
  children: []
}, {
  value: 'Dependencies',
  id: 'dependencies',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const Link = makeShortcode("Link");
const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`The pages that follow contain technical reference information on the Uniswap SDK.
Looking for a `}<Link to='/docs/v2/javascript-SDK/quick-start' mdxType="Link">{`quickstart`}</Link>{` instead?
You may also want to jump into a `}<Link to='/docs/v2/javascript-SDK/fetching-data' mdxType="Link">{`guide`}</Link>{`,
which offers a friendlier introduction to the SDK!`}</p>
    <p>{`The SDK is written in TypeScript, has a robust test suite, performs arbitrary precision arithmetic,
and supports rounding to significant digits or fixed decimal places.
The principal exports of the SDK are `}<em parentName="p">{`entities`}</em>{`: classes that contain initialization and validation checks,
necessary data fields, and helper functions.`}</p>
    <p>{`An important concept in the SDK is `}<em parentName="p">{`fractions`}</em>{`. Because Solidity performs integer math, care must be taken in
non-EVM environments to faithfully replicate the actual computation carried out on-chain.
The first concern here is to ensure that an overflow-safe integer implementation is used.
Ideally, the SDK would be able to use native `}<a parentName="p" {...{
        "href": "https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/BigInt"
      }}>{`BigInt`}</a>{`s.
However, until support becomes more widespread, `}<a parentName="p" {...{
        "href": "https://github.com/GoogleChromeLabs/jsbi"
      }}>{`JSBI`}</a>{` objects are used instead,
with the idea that once BigInts proliferate, this dependency can be compiled away.
The second concern is precision loss due to, for example, chained price ratio calculations.
To address this issue, all math operations are performed as fraction operations, ensuring arbitrary precision up
until the point that values are rounded for display purposes, or truncated to fit inside a fixed bit width.`}</p>
    <p>{`The SDK works for all chains on which the `}<Link to='/docs/v2/smart-contracts/factory#address' mdxType="Link">{`factory`}</Link>{` is deployed.`}</p>
    <h2 {...{
      "id": "code"
    }}>{`Code`}</h2>
    <p>{`The `}<a parentName="p" {...{
        "href": "https://github.com/Uniswap/uniswap-sdk"
      }}>{`source code is available on GitHub`}</a>{`.`}</p>
    <h2 {...{
      "id": "dependencies"
    }}>{`Dependencies`}</h2>
    <p>{`The SDK declares its dependencies as `}<a parentName="p" {...{
        "href": "https://github.com/Uniswap/uniswap-sdk/blob/v2/package.json#L33"
      }}>{`peer dependencies`}</a>{`.
This is for two reasons:`}</p>
    <ul>
      <li parentName="ul">{`prevent installation of unused dependencies (e.g. `}<inlineCode parentName="li">{`@ethersproject/providers`}</inlineCode>{` and `}<inlineCode parentName="li">{`@ethersproject/contracts`}</inlineCode>{`, only used in `}<a parentName="li" {...{
          "href": "/docs/v2/SDK/fetcher"
        }}><inlineCode parentName="a">{`Fetcher`}</inlineCode></a>{`)`}</li>
      <li parentName="ul">{`prevent duplicate `}<inlineCode parentName="li">{`@ethersproject`}</inlineCode>{` dependencies with conflicting versions`}</li>
    </ul>
    <p>{`However, this means you must install these dependencies alongside the SDK, if you do not already have them installed.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
