  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: '01-overview',
	title: 'API Overview',
	tags: 'api, documentation'
};
export const metadata = {
  "unversionedId": "reference/API/01-overview",
  "id": "reference/API/01-overview",
  "isDocsHomePage": false,
  "title": "API Overview",
  "description": "This section explains the Uniswap Subgraph and how to interact with it. The Uniswap subgraph indexes data from the Uniswap contracts over time. It organizes data about pairs, tokens, Uniswap as a whole, and more. The subgraph updates any time a transaction is made on Uniswap. The subgraph runs on The Graph protocol's hosted service and can be openly queried.",
  "source": "@site/docs/reference/API/01-overview.md",
  "slug": "/reference/API/01-overview",
  "permalink": "/docs/next/reference/API/01-overview",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs/reference/API/01-overview.md",
  "version": "current"
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Resources',
  id: 'resources',
  children: []
}, {
  value: 'Usage',
  id: 'usage',
  children: []
}, {
  value: 'Making Queries',
  id: 'making-queries',
  children: []
}, {
  value: 'Versions',
  id: 'versions',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`This section explains the Uniswap Subgraph and how to interact with it. The Uniswap subgraph indexes data from the Uniswap contracts over time. It organizes data about pairs, tokens, Uniswap as a whole, and more. The subgraph updates any time a transaction is made on Uniswap. The subgraph runs on `}<a parentName="p" {...{
        "href": "https://thegraph.com/"
      }}>{`The Graph`}</a>{` protocol's hosted service and can be openly queried.`}</p>
    <h2 {...{
      "id": "resources"
    }}>{`Resources`}</h2>
    <p><a parentName="p" {...{
        "href": "https://thegraph.com/explorer/subgraph/uniswap/uniswap-v2"
      }}>{`Subgraph Explorer`}</a>{` - sandbox for querying data and endpoints for developers.`}</p>
    <p><a parentName="p" {...{
        "href": "https://github.com/Uniswap/uniswap-v2-subgraph"
      }}>{`Uniswap V2 Subgraph`}</a>{` - source code for deployed subgraph.`}</p>
    <h2 {...{
      "id": "usage"
    }}>{`Usage`}</h2>
    <p>{`The subgraph provides a snapshot of the current state of Uniswap and also tracks historical data. It is currently used to power `}<a parentName="p" {...{
        "href": "https://uniswap.info/"
      }}>{`uniswap.info`}</a>{`. `}<strong parentName="p">{`It is not intended to be used as a data source for structuring transactions (contracts should be referenced directly for the most reliable live data).`}</strong></p>
    <h2 {...{
      "id": "making-queries"
    }}>{`Making Queries`}</h2>
    <p>{`To learn more about querying a subgraph refer to `}<a parentName="p" {...{
        "href": "https://thegraph.com/docs/introduction"
      }}>{`The Graph's documentation`}</a>{`.`}</p>
    <h2 {...{
      "id": "versions"
    }}>{`Versions`}</h2>
    <p>{`The `}<a parentName="p" {...{
        "href": "https://thegraph.com/explorer/subgraph/uniswap/uniswap-v2"
      }}>{`Uniswap V2 Subgraph`}</a>{` only tracks data on Uniswap V2. For Uniswap V1 information see the `}<a parentName="p" {...{
        "href": "https://thegraph.com/explorer/subgraph/graphprotocol/uniswap"
      }}>{`V1 Subgraph`}</a>{`.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
