  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: '03-iframe-integration',
	title: 'Iframe Integration',
	tags: 'frontend integration, documentation'
};
export const metadata = {
  "unversionedId": "guides/interface-integration/03-iframe-integration",
  "id": "guides/interface-integration/03-iframe-integration",
  "isDocsHomePage": false,
  "title": "Iframe Integration",
  "description": "Uniswap can be used within other sites as an iframe. An iframe shows an exact version of the uniswap frontend site and can have custom prefilled settings.",
  "source": "@site/docs/guides/interface-integration/03-iframe-integration.md",
