  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const Link = makeShortcode("Link");
const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Uniswap can be used within other sites as an iframe. An iframe shows an exact version of the uniswap frontend site and can have custom prefilled settings.`}</p>
    <h1 {...{
      "id": "why-you-may-want-this"
    }}>{`Why You May Want This`}</h1>
    <p>{`Integrating the Uniswap site directly into your web application can be useful for a variety of reasons.`}</p>
    <p>{`The interface allows users to buy, sell, send, or provide liquidity for ERC20 tokens. An iframe integration may be useful if your application provides services around these ERC20 tokens. `}{`(`}{`For example, users can buy DAI through a Uniswap iframe on your site, then allow users to lend that DAI on your site`}{`)`}{`.`}</p>
    <p>{`It can also be useful if your application requires users to acquire some token in order to use some service `}{`(`}{`For example, allow users to buy "REP" token so they can engage in prediction markets on the Augur Dapp`}{`)`}{`.`}</p>
    <h1 {...{
      "id": "iframe-vs-custom-ui"
    }}>{`iframe vs. custom UI`}</h1>
    <p>{`One benefit of an iframe integration is that the your site will automatically keep up with any improvements/additions to the site. After the initital integration is setup no further work is needed to pull in updates as the exchange site is updated over time.`}</p>
    <h1 {...{
      "id": "live-example"
    }}>{`Live Example`}</h1>
    <iframe src="https://app.uniswap.org/#/swap?exactField=input&exactAmount=10&inputCurrency=0x6b175474e89094c44da98b954eedeac495271d0f" height="660px" width="100%" style="
    border: 0;
    margin: 0 auto;
    margin-bottom: .5rem;
    display: block;
    border-radius: 10px;
    max-width: 960px;
    min-width: 300px;
  " />
    <p>{`An example of an Iframe integration can be found on the FOAM site `}<a parentName="p" {...{
        "href": "https://map.foam.space/#/at/?lng=-74.0045300&lat=40.6771800&zoom=5.00"
      }}>{`https://map.foam.space/`}</a></p>
    <p>{`To see the Iframe click the dropdown in the top right and click "get foam".`}</p>
    <h1 {...{
      "id": "add-to-your-site"
    }}>{`Add To Your Site`}</h1>
    <p>{`To include a Uniswap iframe within your site just add an iframe element within your website code and link to the Uniswap frontent.`}</p>
    <p>{`Linking to a ETH `}{`<`}{`-`}{`>`}{` DAI swap page would look something like this. To link to a token of your choice replace the address after "outputCurrency" with the token address of the token you want to link to.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`<iframe
  src="https://app.uniswap.org/#/swap?outputCurrency=0x89d24a6b4ccb1b6faa2625fe562bdd9a23260359"
  height="660px"
  width="100%"
  style="
    border: 0;
    margin: 0 auto;
    display: block;
    border-radius: 10px;
    max-width: 600px;
    min-width: 300px;
  "
  id="myId"
/>
`}</code></pre>
    <p>{`You can customize the selected page, selected custom tokens and more using URL query parameters. See `}<Link to='/docs/v2/interface-integration/custom-interface-linking' mdxType="Link">{`Custom Linking`}</Link>{`.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
