  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: '01-getting-started',
	title: 'Getting Started'
};
export const metadata = {
  "unversionedId": "V1/02-SDK/01-getting-started",
  "id": "version-V1/V1/02-SDK/01-getting-started",
  "isDocsHomePage": false,
  "title": "Getting Started",
  "description": "Uniswap V1, while still fully functional, is no longer under active development. Looking for V2 documentation?",
  "source": "@site/docs_versioned_docs/version-V1/V1/02-SDK/01-getting-started.md",
  "slug": "/V1/02-SDK/01-getting-started",
  "permalink": "/docs/V1/02-SDK/01-getting-started",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs_versioned_docs/version-V1/V1/02-SDK/01-getting-started.md",
  "version": "V1",
  "sidebar": "version-V1/docs",
  "previous": {
    "title": "Token Listing",
    "permalink": "/docs/V1/01-frontend-integration/06-token-listing"
  },
  "next": {
    "title": "Data",
    "permalink": "/docs/V1/02-SDK/02-data"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const Info = makeShortcode("Info");
const Link = makeShortcode("Link");
const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <Info mdxType="Info">
  Uniswap V1, while still fully functional, is no longer under active development. Looking for <Link style={{
        display: "contents"
      }} to='/docs/v2/' mdxType="Link">V2 documentation</Link>?
    </Info>
    <p>{`The `}<a parentName="p" {...{
        "href": "https://github.com/Uniswap/uniswap-sdk/tree/v1"
      }}>{`Uniswap SDK`}</a>{` is meant to simplify every aspect of integrating Uniswap into your project. It's written in `}<a parentName="p" {...{
        "href": "https://www.typescriptlang.org"
      }}>{`TypeScript`}</a>{`, has a `}<a parentName="p" {...{
        "href": "https://github.com/Uniswap/uniswap-sdk/tree/v1/src/__tests__"
      }}>{`robust test suite`}</a>{`, uses `}<a parentName="p" {...{
        "href": "https://github.com/MikeMcl/bignumber.js"
      }}>{`bignumber.js`}</a>{` for math, and includes an optional data-fetching module which relies on `}<a parentName="p" {...{
        "href": "https://github.com/ethers-io/ethers.js/"
      }}>{`ethers.js`}</a>{`.`}</p>
    <p>{`The SDK was built to be extremely easy to use, but also feature-rich. It offers various levels of abstraction that make it suitable for use nearly anywhere, from hackathon projects to production applications.`}</p>
    <h1 {...{
      "id": "overview"
    }}>{`Overview`}</h1>
    <p>{`The SDK is divided into several modular components that perform tightly scoped tasks:`}</p>
    <ul>
      <li parentName="ul">
        <Link to='/docs/v1/SDK/data' mdxType="Link">Data</Link> - Fetches Uniswap data from the blockchain
      </li>
      <li parentName="ul">
        <Link to='/docs/v1/SDK/computation' mdxType="Link">Computation</Link> - Computes market- and trade-specific statistics using blockchain data
      </li>
      <li parentName="ul">
        <Link to='/docs/v1/SDK/format' mdxType="Link">Format</Link> - Formats data for display
      </li>
      <li parentName="ul">
        <Link to='/docs/v1/SDK/orchestration' mdxType="Link">Orchestration</Link> - Offers named abstraction functions that seamlessly combine lower-level data- and computation-related functions
      </li>
      <li parentName="ul">
        <Link to='/docs/v1/SDK/transact' mdxType="Link">Transact</Link> - Prepares computed trades for execution against Uniswap smart contracts
      </li>
      <li parentName="ul">
        <Link to='/docs/v1/SDK/constants' mdxType="Link">Constants</Link> - Exports various helpful constants for use throughout the SDK
      </li>
    </ul>
    <p>{`Additionally, it exports a number of custom types:`}</p>
    <ul>
      <li parentName="ul">
        <Link to='/docs/v1/SDK/types' mdxType="Link">Types</Link> - Exports all types used by the SDK
      </li>
    </ul>
    <h1 {...{
      "id": "installation"
    }}>{`Installation`}</h1>
    <p>{`To start using the SDK, simply install it into your project...`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`yarn add @uniswap/sdk
`}</code></pre>
    <p>{`...import some functions...`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`import { ... } from '@uniswap/sdk'
`}</code></pre>
    <p>{`...and dive into the rest of the documentation to learn more!`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
