const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const Link = makeShortcode("Link");
const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`This documentation covers Uniswap-specific functionality. For ERC-20 functionality, see `}<Link to='/docs/v2/smart-contracts/pair-erc-20' mdxType="Link">{`Pair (ERC-20)`}</Link>{`.`}</p>
    <h1 {...{
      "id": "code"
    }}>{`Code`}</h1>
    <p><a parentName="p" {...{
        "href": "https://github.com/Uniswap/uniswap-v2-core/blob/master/contracts/UniswapV2Pair.sol"
      }}><inlineCode parentName="a">{`UniswapV2Pair.sol`}</inlineCode></a></p>
    <h1 {...{
      "id": "address"
    }}>{`Address`}</h1>
    <p>{`See `}<Link to='/docs/v2/smart-contract-integration/getting-pair-addresses/' mdxType="Link">{`Pair Addresses`}</Link>{`.`}</p>
    <h1 {...{
      "id": "events"
    }}>{`Events`}</h1>
    <h2 {...{
      "id": "mint"
    }}>{`Mint`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`event Mint(address indexed sender, uint amount0, uint amount1);
`}</code></pre>
    <p>{`Emitted each time liquidity tokens are created via `}<a parentName="p" {...{
        "href": "#mint-1"
      }}>{`mint`}</a>{`.`}</p>
    <h2 {...{
      "id": "burn"
    }}>{`Burn`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`event Burn(address indexed sender, uint amount0, uint amount1, address indexed to);
`}</code></pre>
    <p>{`Emitted each time liquidity tokens are destroyed via `}<a parentName="p" {...{
        "href": "#burn-1"
      }}>{`burn`}</a>{`.`}</p>
    <h2 {...{
      "id": "swap"
    }}>{`Swap`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`event Swap(
  address indexed sender,
  uint amount0In,
  uint amount1In,
  uint amount0Out,
  uint amount1Out,
  address indexed to
);
`}</code></pre>
    <p>{`Emitted each time a swap occurs via `}<a parentName="p" {...{
        "href": "#swap-1"
      }}>{`swap`}</a>{`.`}</p>
    <h2 {...{
      "id": "sync"
    }}>{`Sync`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`event Sync(uint112 reserve0, uint112 reserve1);
`}</code></pre>
    <p>{`Emitted each time reserves are updated via `}<a parentName="p" {...{
        "href": "#mint-1"
      }}>{`mint`}</a>{`, `}<a parentName="p" {...{
        "href": "#burn-1"
      }}>{`burn`}</a>{`, `}<a parentName="p" {...{
        "href": "#swap-1"
      }}>{`swap`}</a>{`, or `}<a parentName="p" {...{
        "href": "#sync-1"
      }}>{`sync`}</a>{`.`}</p>
    <h1 {...{
      "id": "read-only-functions"
    }}>{`Read-Only Functions`}</h1>
    <h2 {...{
      "id": "minimum_liquidity"
    }}>{`MINIMUM_LIQUIDITY`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function MINIMUM_LIQUIDITY() external pure returns (uint);
`}</code></pre>
    <p>{`Returns `}<inlineCode parentName="p">{`1000`}</inlineCode>{` for all pairs. See `}<Link to='/docs/v2/protocol-overview/smart-contracts/#minimum-liquidity' mdxType="Link">{`Minimum Liquidity`}</Link>{`.`}</p>
    <h2 {...{
      "id": "factory"
    }}>{`factory`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function factory() external view returns (address);
`}</code></pre>
    <p>{`Returns the `}<Link to='/docs/v2/smart-contracts/factory/#address' mdxType="Link">{`factory address`}</Link>{`.`}</p>
    <h2 {...{
      "id": "token0"
    }}>{`token0`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function token0() external view returns (address);
`}</code></pre>
    <p>{`Returns the address of the pair token with the lower sort order.`}</p>
    <h2 {...{
      "id": "token1"
    }}>{`token1`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function token1() external view returns (address);
`}</code></pre>
    <p>{`Returns the address of the pair token with the higher sort order.`}</p>
    <h2 {...{
      "id": "getreserves"
    }}>{`getReserves`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
`}</code></pre>
    <p>{`Returns the reserves of token0 and token1 used to price trades and distribute liquidity. See `}<Link to='/docs/v2/advanced-topics/pricing/' mdxType="Link">{`Pricing`}</Link>{`. Also returns the `}<inlineCode parentName="p">{`block.timestamp`}</inlineCode>{` (mod `}<inlineCode parentName="p">{`2**32`}</inlineCode>{`) of the last block during which an interaction occured for the pair.`}</p>
    <h2 {...{
      "id": "price0cumulativelast"
    }}>{`price0CumulativeLast`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function price0CumulativeLast() external view returns (uint);
`}</code></pre>
    <p>{`See `}<Link to='/docs/v2/core-concepts/oracles/' mdxType="Link">{`Oracles`}</Link>{`.`}</p>
    <h2 {...{
      "id": "price1cumulativelast"
    }}>{`price1CumulativeLast`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function price1CumulativeLast() external view returns (uint);
`}</code></pre>
    <p>{`See `}<Link to='/docs/v2/core-concepts/oracles/' mdxType="Link">{`Oracles`}</Link>{`.`}</p>
    <h2 {...{
      "id": "klast"
    }}>{`kLast`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function kLast() external view returns (uint);
`}</code></pre>
    <p>{`Returns the product of the reserves as of the most recent liquidity event. See `}<Link to='/docs/v2/advanced-topics/fees/#protocol-charge-calculation' mdxType="Link">{`Protocol Charge Calculation`}</Link>{`.`}</p>
    <h1 {...{
      "id": "state-changing-functions"
    }}>{`State-Changing Functions`}</h1>
    <h2 {...{
      "id": "mint-1"
    }}>{`mint`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function mint(address to) external returns (uint liquidity);
`}</code></pre>
    <p>{`Creates pool tokens.`}</p>
    <ul>
      <li parentName="ul">{`Emits `}<a parentName="li" {...{
          "href": "#mint"
        }}>{`Mint`}</a>{`, `}<a parentName="li" {...{
          "href": "#sync"
        }}>{`Sync`}</a>{`, `}<Link to='/docs/v2/smart-contracts/pair-erc-20#transfer' mdxType="Link">{`Transfer`}</Link>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "burn-1"
    }}>{`burn`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function burn(address to) external returns (uint amount0, uint amount1);
`}</code></pre>
    <p>{`Destroys pool tokens.`}</p>
    <ul>
      <li parentName="ul">{`Emits `}<a parentName="li" {...{
          "href": "#burn"
        }}>{`Burn`}</a>{`, `}<a parentName="li" {...{
          "href": "#sync"
        }}>{`Sync`}</a>{`, `}<Link to='/docs/v2/smart-contracts/pair-erc-20#transfer' mdxType="Link">{`Transfer`}</Link>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "swap-1"
    }}>{`swap`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
`}</code></pre>
    <p>{`Swaps tokens. For regular swaps, `}<inlineCode parentName="p">{`data.length`}</inlineCode>{` must be `}<inlineCode parentName="p">{`0`}</inlineCode>{`. Also see `}<Link to='/docs/v2/core-concepts/flash-swaps/' mdxType="Link">{`Flash Swaps`}</Link>{`.`}</p>
    <ul>
      <li parentName="ul">{`Emits `}<a parentName="li" {...{
          "href": "#swap"
        }}>{`Swap`}</a>{`, `}<a parentName="li" {...{
          "href": "#sync"
        }}>{`Sync`}</a>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "skim"
    }}>{`skim`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function skim(address to) external;
`}</code></pre>
    <p>{`See the `}<a href='/whitepaper.pdf' target='_blank' rel='noopener noreferrer'>{`whitepaper`}</a>{`.`}</p>
    <h2 {...{
      "id": "sync-1"
    }}>{`sync`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`function sync() external;
`}</code></pre>
    <p>{`See the `}<a href='/whitepaper.pdf' target='_blank' rel='noopener noreferrer'>{`whitepaper`}</a>{`.`}</p>
    <ul>
      <li parentName="ul">{`Emits `}<a parentName="li" {...{
          "href": "#sync"
        }}>{`Sync`}</a>{`.`}</li>
    </ul>
    <h1 {...{
      "id": "interface"
    }}>{`Interface`}</h1>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol';
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`pragma solidity >=0.5.0;

interface IUniswapV2Pair {
  event Approval(address indexed owner, address indexed spender, uint value);
  event Transfer(address indexed from, address indexed to, uint value);

  function name() external pure returns (string memory);
  function symbol() external pure returns (string memory);
  function decimals() external pure returns (uint8);
  function totalSupply() external view returns (uint);
  function balanceOf(address owner) external view returns (uint);
  function allowance(address owner, address spender) external view returns (uint);

  function approve(address spender, uint value) external returns (bool);
  function transfer(address to, uint value) external returns (bool);
  function transferFrom(address from, address to, uint value) external returns (bool);

  function DOMAIN_SEPARATOR() external view returns (bytes32);
  function PERMIT_TYPEHASH() external pure returns (bytes32);
  function nonces(address owner) external view returns (uint);

  function permit(address owner, address spender, uint value, uint deadline, uint8 v, bytes32 r, bytes32 s) external;

  event Mint(address indexed sender, uint amount0, uint amount1);
  event Burn(address indexed sender, uint amount0, uint amount1, address indexed to);
  event Swap(
      address indexed sender,
      uint amount0In,
      uint amount1In,
      uint amount0Out,
      uint amount1Out,
      address indexed to
  );
  event Sync(uint112 reserve0, uint112 reserve1);

  function MINIMUM_LIQUIDITY() external pure returns (uint);
  function factory() external view returns (address);
  function token0() external view returns (address);
  function token1() external view returns (address);
  function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
  function price0CumulativeLast() external view returns (uint);
  function price1CumulativeLast() external view returns (uint);
  function kLast() external view returns (uint);

  function mint(address to) external returns (uint liquidity);
  function burn(address to) external returns (uint amount0, uint amount1);
  function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
  function skim(address to) external;
  function sync() external;
}
`}</code></pre>
    <h1 {...{
      "id": "abi"
    }}>{`ABI`}</h1>
    <pre><code parentName="pre" {...{
        "className": "language-typescript"
      }}>{`import IUniswapV2Pair from '@uniswap/v2-core/build/IUniswapV2Pair.json'
`}</code></pre>
    <p><a parentName="p" {...{
        "href": "https://unpkg.com/@uniswap/v2-core@1.0.0/build/IUniswapV2Pair.json"
      }}>{`https://unpkg.com/@uniswap/v2-core@1.0.0/build/IUniswapV2Pair.json`}</a></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
