  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: '07-supporting-meta-transactions',
	title: 'Supporting meta transactions'
};
export const metadata = {
  "unversionedId": "guides/smart-contract-integration/07-supporting-meta-transactions",
  "id": "version-V2/guides/smart-contract-integration/07-supporting-meta-transactions",
  "isDocsHomePage": false,
  "title": "Supporting meta transactions",
  "description": "All Uniswap V2 pool tokens support meta-transaction approvals via the permit function. This obviates the need for a blocking approve transaction before programmatic interactions with pool tokens can occur.",
  "source": "@site/docs_versioned_docs/version-V2/guides/smart-contract-integration/07-supporting-meta-transactions.md",
  "slug": "/guides/smart-contract-integration/07-supporting-meta-transactions",
  "permalink": "/docs/V2/guides/smart-contract-integration/07-supporting-meta-transactions",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs_versioned_docs/version-V2/guides/smart-contract-integration/07-supporting-meta-transactions.md",
  "version": "V2",
  "sidebar": "version-V2/guides",
  "previous": {
    "title": "Pair Addresses",
    "permalink": "/docs/V2/guides/smart-contract-integration/06-getting-pair-addresses"
  },
  "next": {
    "title": "Using the API",
    "permalink": "/docs/V2/guides/interface-integration/01-using-the-api"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'Domain Separator',
  id: 'domain-separator',
  children: []
}, {
  value: 'Permit Typehash',
  id: 'permit-typehash',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
      console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
      return <div {...props}/>
    };
const Link = makeShortcode("Link");
const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`All Uniswap V2 pool tokens support meta-transaction approvals via the `}<Link to='/docs/v2/smart-contracts/pair-erc-20#permit' mdxType="Link">{`permit`}</Link>{` function. This obviates the need for a blocking approve transaction before programmatic interactions with pool tokens can occur.`}</p>
    <h1 {...{
      "id": "erc-712"
    }}>{`ERC-712`}</h1>
    <p>{`In vanilla ERC-20 token contracts, owners may only register approvals by directly calling a function which uses `}<inlineCode parentName="p">{`msg.sender`}</inlineCode>{` to permission itself. With meta-approvals, ownership and permissioning are derived from a signature passed into the function by the caller (sometimes referred to as the relayer). Because signing data with Ethereum private keys can be a tricky endeavor, Uniswap V2 relies on `}<a parentName="p" {...{
        "href": "https://eips.ethereum.org/EIPS/eip-712"
      }}>{`ERC-712`}</a>{`, a signature standard with widespread community support, to ensure user safety and wallet compatibility.`}</p>
    <h2 {...{
      "id": "domain-separator"
    }}>{`Domain Separator`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`keccak256(
  abi.encode(
    keccak256('EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)'),
    keccak256(bytes(name)),
    keccak256(bytes('1')),
    chainId,
    address(this)
  )
);
`}</code></pre>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`name`}</inlineCode>{` is always `}<inlineCode parentName="li">{`Uniswap V2`}</inlineCode>{`, see `}<Link to='/docs/v2/smart-contracts/pair-erc-20#name' mdxType="Link">{`name`}</Link>{`.`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`chainId`}</inlineCode>{` is determined from the `}<a parentName="li" {...{
          "href": "https://ethereum-magicians.org/t/eip-1344-add-chain-id-opcode/1131"
        }}>{`ERC-1344`}</a>{` `}<inlineCode parentName="li">{`chainid`}</inlineCode>{` opcode.`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`address(this)`}</inlineCode>{` is the address of the pair, see `}<Link to='/docs/v2/javascript-SDK/getting-pair-addresses/' mdxType="Link">{`Pair Addresses`}</Link>{`.`}</li>
    </ul>
    <h2 {...{
      "id": "permit-typehash"
    }}>{`Permit Typehash`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-solidity"
      }}>{`keccak256('Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)');\`
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
