export const toc = [{
  value: 'Function Signature',
  id: 'function-signature',
  children: []
}, {
  value: 'Input Parameters',
  id: 'input-parameters',
  children: []
}, {
  value: 'Example Usage',
  id: 'example-usage',
  children: []
}, {
  value: 'Function Signature',
  id: 'function-signature-1',
  children: []
}, {
